sys_variable_value.value:
(function execute(inputs, outputs) {
    inputs.container_name = inputs.container_name.trim();
    if (!inputs.container_name || inputs.container_name.length == 0)
        throw new Error("Empty value passed in Container Name");
  	outputs.container_name = new AzureBlobStorageUtils().ValidateContainerName(inputs.container_name);

    inputs.blob_name = inputs.blob_name.trim();
    if (!inputs.blob_name || inputs.blob_name.length == 0)
        throw new Error("Empty value passed in Blob Name");
    outputs.blob_name = inputs.blob_name;

    if (inputs.download_as)
        outputs.download_as = inputs.download_as;
    else
        outputs.download_as = inputs.blob_name;

})(inputs, outputs);

