sys_variable_value.value:
(function execute(inputs, outputs) {
  if(inputs.status == 200){
    outputs.status="Success";
  }
  else{
    outputs.status = "Error";
    var xmlDoc = new XMLDocument2();
    var index = inputs.response_body.indexOf(">");
    var xml = inputs.response_body.slice(index + 1, inputs.response_body
            .length);
    xmlDoc.parseXML(xml);
    var message = xmlDoc.getNodeText("/Error/Message");
    var index = message.indexOf("RequestId");
    var error = message.slice(0, index);
    outputs.error_message = error;
  }
})(inputs, outputs);

